body, #root {
  height: 100%;
  width: 100%;
  margin: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: system-ui, sans-serif;
  background: #f8fafc;
}

.hello-container {
  font-size: 2rem;
  color: #333;
  background: #fff;
  padding: 32px 48px;
  border-radius: 16px;
  box-shadow: 0 4px 24px 0 rgba(0, 0, 0, 0.0666666667);
  font-weight: 600;
  letter-spacing: 1px;
}

srcDoc.js:
// Vanilla JS: Render data from 'tips' variable if present, else fallback
appsmith.onReady(function () {
  const root = document.getElementById('root');
  const container = document.createElement('div');
  container.className = 'hello-container';
  // Fallback if tips is not present
  const tips1 = appsmith.model.sisu1 ?? 'No sisu1 available';
  container.textContent = tips1;
  root.appendChild(container);
  const container2 = document.createElement('div2');
  container2.className = 'hello-container';
  // Fallback if tips is not present
  const tips2 = appsmith.model.sisu2 ?? 'No sisu2 available';
  container2.textContent = tips2;
  root.appendChild(container2);

  // Listen to changes in the model to update the tip dynamically
  /*
  appsmith.onModelChange(function(model, prevModel) {
    if (model.tips !== prevModel?.tips) {
      container.textContent = model.tips ?? 'No tip available';
    }
  });
  */
});

